mapper.select:
select d.id, d.name, d.category_id, c.name as category_name, d.price, d.description, d.image, d.status, d.update_time from dish d left join category c on d.category_id = c.id
and d.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.update_time desc

select * from dish


        and name like concat('%', #{name}, '%')


        and category_id = #{categoryId}


        and price = #{price}


        and status = #{status}
