mapper.update:
update setmeal set status = #{status}
where id in

    #{id}

mapper.select:
select s.id,s.category_id,s.name,s.price,s.status,s.description,s.image,s.update_time,c.name category_name from setmeal s join category c on s.category_id = c.id


        and s.name like concat('%', #{name}, '%')


        and s.category_id = #{categoryId}


        and s.status = #{status}


order by s.update_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
